import react, { useEffect, useState } from "react";
import "./App.css";
import MenuBookIcon from '@material-ui/icons/MenuBook';
import PeopleIcon from '@material-ui/icons/People';
import PaymentIcon from '@material-ui/icons/Payment';


function App() {
  const [books, setbooks] = useState([]);
  useEffect(() => {
    setInterval(() => {
      fetch("./api/books")
        .then((res) => res.json())
        .then((data) => {
          console.log(data);
          setbooks(data);
        });
    }, 2000);
  }, []);

  const addbook = () => {
    const title = prompt("Enter Book title");
    const auther = prompt("Enter Auther name");
    const price = prompt("Enter Price ");
    console.log(title, auther, price);
    fetch("api/add", {
      method: "POST",
      body: JSON.stringify({ title, auther, price }),
    }).catch((error) => {
      console.log("Error", error);
    });
  };
  if (!books.length) return <h2>Loading!</h2>;

  return (
    <div class="container">

      <h2>UOS Library </h2>
      <button  onClick={addbook} className='btn'>ADD Book</button>

      <li class="table-header">
        <div class="col col-1">Book title  </div>
        <div class="col col-2"> Auther  Name </div>
        <div class="col col-3">Price  </div>
      </li>

      {books.map((BooksObj, ind) => {
        return (
          <li class="table-row">
            <div class="col col-1" data-label="Job Id"><MenuBookIcon/>
              {BooksObj.title}
            </div>
            <div class="col col-2" data-label="Customer Name"><PeopleIcon/>
              {BooksObj.auther}
            </div>
            <div class="col col-3" data-label="Amount">
              {BooksObj.price}
            </div>
          </li>
        );
      })}



    </div>
  );
}

export default App;
