import react, { useEffect ,useState} from 'react';
import './App.css';



function App() {
  const [books, setbooks] = useState([]);
  useEffect(() => {
     setInterval(() => {
        fetch("./api/books")
      .then(res => res.json())
      .then(data => {
        console.log(data)
        setbooks(data);
      })}, 2000);

  }, [])

const addbook = () =>{
  const title = prompt("Enter Book title")
  const auther = prompt("Enter Auther name")  
  const price = prompt("Enter Price ")
 console.log(title,auther,price);
 fetch('api/add',{
  method: 'POST',
  body: JSON.stringify({title,auther,price})
})
.catch((error)=>{
  console.log("Error",error)
})
}
  if(!books.length)
  return <h2>Loading!</h2>

  return (
    <div class="container" >
       <h2>UOS Library </h2>

      <table border="1">


      <div class="col col-1">Book title</div>
      <div class="col col-2"> Auther Name</div>
      <div class="col col-3">Price </div>
        {books.map((BooksObj, ind) => {
          return (

              <li class="table-row">
      <div class="col col-1" data-label="Job Id">{BooksObj.title}</div>
      <div class="col col-2" data-label="Customer Name">{BooksObj.auther}</div>
      <div class="col col-3" data-label="Amount">{BooksObj.price}</div>

    </li>

          )
        })}
      </table>

      <button onClick ={addbook} >ADD Book</button>


      <div class="container">
  <h2>Responsive Tables Using LI <small>Triggers on 767px</small></h2>
  <ul class="responsive-table">
    <li class="table-header">
      <div class="col col-1">Book title</div>
      <div class="col col-2"> Auther Name</div>
      <div class="col col-3">Price </div>

    </li>
    <li class="table-row">
      {/* <div class="col col-1" data-label="Job Id">{BooksObj.title}</div>
      <div class="col col-2" data-label="Customer Name">{BooksObj.auther}</div>
      <div class="col col-3" data-label="Amount">{BooksObj.price}</div> */}

    </li>

  </ul>
</div>




    </div>

  );
}

export default App;
