import react, { useEffect ,useState} from 'react';
import './App.css';



function App() {
  const [books, setbooks] = useState([]);
  useEffect(() => {
    fetch("./api/books")
      .then(res => res.json())
      .then(data => {
        console.log(data)
        setbooks(data);
      })

  }, [])

const addbook = () =>{
  const title = prompt("Enter Book title")
  const auther = prompt("Enter Auther name")  
  const price = prompt("Enter Price ")
 console.log(title,auther,price);
fetch("./api/add", {
  method :"POST",
  body :JSON.stringfy({title,auther,price})
})
.then(res.res.json)

}

  if(!books.length)
  return <h2>Loading!</h2>

  return (
    <div >

      <table border="1">
        <th>Book-Name</th>
        <th>Book-Auther</th>
        <th>Book-price</th>
        {books.map((BooksObj, ind) => {
          return (
            <tr>
              <td>{BooksObj.title}</td>
              <td>{BooksObj.auther}</td>
          <td>{BooksObj.price}</td>
            </tr>
          )
        })}
      </table>

      <button onClick ={addbook} >ADD Book</button>




    </div>

  );
}

export default App;
